function delDiv(elm){
	elm.parentElement.removeChild(elm);
}
function set_hrefLink(){
	profile = document.getElementById("username").innerText.replace('@','');
	document.getElementById("username").href = `https://t.me/${profile}`; 
}

function setTextinElm(elm,text){
	elm.innerText = `${text}`;
}

function changeElementTextById(newText, elementId) {
  if (document.getElementById(elementId)) {
    document.getElementById(elementId).innerHTML = newText;
  }
}