function delDiv(elm){
	elm.parentElement.removeChild(elm);
}
function set_hrefLink(){
	profile = document.getElementById("username").innerText.replace('@','');
	document.getElementById("username").href = `https://t.me/${profile}`; 
}

function setTextinElm(elm,text){
	elm.innerText = `${text}`;
}

function changeElementTextById(newText, elementId) {
  if (document.getElementById(elementId)) {
    document.getElementById(elementId).innerHTML = newText;
  }
}


async function sendFetchRequest(tableName, queryParam, numberParam) {
  const url = `/api/?query=${queryParam}&table=${tableName}`;
  const numberToSend = numberParam || 5; // Значение по умолчанию - 5

  try {
    const response = await fetch(url);
    const data = await response.json();
    if (data) {
      console.log(data);
      return data;
    } else {
      throw new Error('No data or count property found in the response.');
    }
  } catch (error) {
    console.error(`Ошибка:`, error);
    throw error;
  }
}
